ow2=mysql_fetch_array($r2);						?>						<table>							<tr>								<th>Set Batch</th>								<td><input type="text" value="<?php echo $row[0]; ?>" id="bat" size="4" maxlength="4" disabled="disabled" /></td>							</tr>							<tr>								<th>Error Allowed</th>								<td><input type="text" value="<?php echo $row2[0]; ?>" id="err" size="4" maxlength="8" disabled="disabled" /></td>							</tr>							<!--							<tr>								<th>Show Answer</th>								<td><input type="text" value="<?php echo $row2[0]; ?>" id="err" size="4" maxlength="8" disabled="disabled" /></td>							</tr>							-->							<tr>								<td colspan="2" ><input type="button" value="Edit" onclick="edit_set(this);" /></td>							</tr>						</table>					</div>					<div>						<?php							$r3=executeQuery("SELECT `metadata` FROM `sett` WHERE `key`='notice'");							$row=mysql_fetch_array($r3);						?>						<table>							<tr>								<th>NOTICE and UPDATES</th>							</tr>							<tr>								<td><textarea cols="80" rows="15" id="mdata" disabled="disabled" ><?php echo $row[0]; ?></textarea></td>							</tr>							<tr>								<td><input type="button" value="Edit" id="but1" onclick="enbArea();" /></td>							</tr>						</table>						<br/><br/>					</div>			</div>		</div><script>	function enbArea()	{		$('#but1').val('Update');		$('#but1').attr('onclick','update_notice()');		$('#mdata').removeAttr('disabled');	}	function update_notice()	{			$.ajax({url:"ajaxhandler.php?request_id=9&m_data="+escape($('#mdata').val()),success:function(result){				if(result == 1)				{					$('#but1').val('Edit');					$('#mdata').attr('disabled','disabled');					$('#but1').attr('onclick','enbArea()');				}				else					alert('Error. Try Again!');			}        });	}	function edit_set(k)	{		$(k).val("Save");		$(k).attr("onclick","save_set(this)");		$('#bat').removeAttr('disabled');		$('#err').removeAttr('disabled');	}	function save_set(k)	{	